mapper.select:
/* CodeMapper.selectCodeList */ SELECT
FROM CM_CODE WHERE CD_TYPE = 'ROOT'
AND CD = #{sCd}
AND CD_NM LIKE '%' || #{sCdNm}|| '%'
ORDER BY CD


/* CodeMapper.selectCodeDtlList */ 
SELECT 

  FROM CM_CODE 
 WHERE CD_TYPE = #{cdType}
ORDER BY SEQ

mapper.insert:

/* CodeMapper.insertCode */ 
INSERT INTO CM_CODE ( 

) VALUES ( 
	#{cdType} 
	, #{cd} 
	, #{cdNm} 
	, #{useYn} 
	, #{seq} 
	, #{etcCd} 
	, #{etcDesc} 
	, #{refVal1} 
	, #{remark} 
	, REPLACE(#{regDtm}, '-', '') 
	, #{userNm} 
	, REPLACE(#{modDtm}, '-', '') 
	, #{userNm} 
)

mapper.update:

/* CodeMapper.updateCode */ 
UPDATE CM_CODE 
SET 
	CD_NM = #{cdNm} 
	, USE_YN = #{useYn} 
	, SEQ = #{seq} 
	, ETC_CD = #{etcCd} 
	, ETC_DESC = #{etcDesc} 
	, REF_VAL1 = #{refVal1} 
	, REMARK = #{remark} 
	, MOD_DTM = SYSDATE 
	, MOD_NM = #{userNm} 
WHERE CD_TYPE = #{cdType} 
  AND CD = #{cd}

mapper.delete:

/* CodeMapper.deleteCode */ 
DELETE FROM CM_CODE 
WHERE CD_TYPE = #{cdType} 
  AND CD = #{cd}
